    if (only !== 'path') regex += str
    if (filepath && only !== 'regex') {
      path.regex += str === '\\/' ? SEP : str
      if (split) {
        if (last) segment += str
        if (segment !== '') {
          if (!flags.includes('g')) segment = `^${segment}$` // change it 'includes'
          path.segments.push(new RegExp(segment, flags))
        }
        segment = ''
      } else {
        segment += str
      }
    }
  }

  let c, n
  for (let i = 0; i < glob.length; i++) {
    c = glob[i]
    n = glob[i + 1]

    if (['\\', '$', '^', '.', '='].includes(c)) {
      add(`\\${c}`)
      continue
    }

    if (c === '/') {
      add(`\\${c}`, { split: true })
      if (n === '/' && !strict) regex += '?'
      continue
    }

    if (c === '(') {
      if (ext.length) {
        add(c)
        continue
      }
      add(`\\${c}`)
      continue
    }

    if (c === ')') {
      if (ext.length) {
        add(c)
        let type = ext.pop()
        if (type === '@') {
          add('{1}')
        } else if (type === '!') {
          add('([^/]*)')
        } else {
          add(type)
        }
        continue
      }
      add(`\\${c}`)
      continue
    }

    if (c === '|') {
      if (ext.length) {
        add(c)
        continue
      }
      add(`\\${c}`)
      continue
    }

    if (c === '+') {
      if (n === '(' && extended) {
        ext.push(c)
        continue
      }
      add(`\\${c}`)
      continue
    }

    if (c === '@' && extended) {
      if (n === '(') {
        ext.push(c)
        continue
      }
    }

    if (c === '!') {
      if (extended) {
        if (inRange) {
          add('^')
          continue
        }
        if (n === '(') {
          ext.push(c)
          add('(?!')
          i++
          continue
        }
        add(`\\${c}`)
        continue
      }
      add(`\\${c}`)
      continue
    }

    if (c === '?') {
      if (extended) {
        if (n === '(') {
          ext.push(c)
        } else {
          add('.')
        }
        continue
      }
      add(`\\${c}`)
      continue
    }

    if (c === '[') {
      if (inRange && n === ':') {
        i++ // skip [
        let value = ''
        while (glob[++i] !== ':') value += glob[i]
